/**
 * File Description: This component is used to display status of flights
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React from 'react';
import styled from 'styled-components';

const Status = ({ to = '', from = '' }) => {
  let displayText = from === undefined ? '' : `${from} to ${to}`;
  return <StyledStatus>{displayText}</StyledStatus>;
};

const StyledStatus = styled.div`
  width: 100%;
  height: 5em;
  border-bottom: 2px solid black;
  position: relative;
  font-size: 20px;
  font-weight: bold;
  color: black;
`;
export default Status;
