/**
 * File Description: This component is a loader component to display that the user is still waiting for results
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React from 'react';
import { Button } from 'react-bootstrap';
import styled from 'styled-components';

const Loader = ({ variant = 'primary', children, className, onClick }) => {
  return (
    <StyledButton className={className} variant={variant} onClick={onClick}>
      {' '}
      {children}{' '}
    </StyledButton>
  );
};

const StyledButton = styled(Button)`
  display: flex;
  color: ${(props) => props.variant};
  width: 100%;
  justify-content: center;
  align-items: center;
`;

export default Loader;
