/**
 * File Description: This component is used to display status of flights
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React, { Fragment } from 'react';
import styled from 'styled-components';
import moment from 'moment';
const Status = ({ to = '', from = '', flightsCount = 0, fromDate = '' }) => {
  console.log(
    moment(fromDate).format('LLLL').replace(moment(fromDate).format('LT'), '')
  );
  let displayText = from === '' ? '' : to === '' ? '' : `${from} to ${to}`;
  return (
    <StyledStatus id="flight-status___container">
      {displayText !== '' && fromDate !== '' ? (
        <Fragment>
          <div id="flight-status__result">{displayText}</div>
          <StyledStatsDiv id="flight-status__stats">
            <div>{`${flightsCount} flights found`}</div>
            <div>{JSON.stringify(fromDate)}</div>
          </StyledStatsDiv>
        </Fragment>
      ) : null}
    </StyledStatus>
  );
};

const StyledStatus = styled.div`
  width: 100%;
  height: 5em;
  display: flex;
  flex-direction: column;
  border-bottom: 2px solid black;
  position: relative;
  font-size: 1em;
  font-weight: bold;
  color: black;

  #flight-status__result {
    width: 30em;
    height: 3em;
    display: flex;
    justify-content: start;
    align-items: center;
    margin-left: 1em;
    background: red;
  }
`;

const StyledStatsDiv = styled.div`
  display: flex;
  width: 100%;
  position: relative;
  font-size: 1em;
  font-weight: normal;
  color: gray;
`;

export default Status;
