/**
 * File Description: This component is the left pane of the website to display the results of search
 * Author(s): Sashank Pindiproli
 * Date of Creation: 02/02/2020
 */

import React, { useState } from 'react';
import styled from 'styled-components';
import ToggleBtnComponent from '../ToggleButton';
import { Form, Button } from 'react-bootstrap';
const LeftPane = () => {
  const radios = [
    { name: 'One Way', value: '1' },
    { name: 'Return', value: '2' },
  ];

  const [radioValue, setRadioValue] = useState(radios[0].value);
  console.log(radioValue);
  return (
    <StyledContainer id="left-pane__container">
      <StyledToggleArea id="flight-travel__options">
        <ToggleBtnComponent
          radios={radios}
          defaultVal={radioValue}
          onClick={(value) => setRadioValue(value)}
        />
      </StyledToggleArea>
      <StyledFormArea>
        <Form>
          <Form.Group controlId="formOriginCity">
            <Form.Control type="text" placeholder="Enter Origin City" />
          </Form.Group>
          <Form.Group controlId="formDestinationCity">
            <Form.Control type="text" placeholder="Enter Destination City" />
          </Form.Group>

          <Form.Group controlId="formDestinationCity"></Form.Group>
          {/* <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group> */}
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </StyledFormArea>
    </StyledContainer>
  );
};

const StyledContainer = styled.div`
  border: 2px solid black;
  width: calc(15%);
  display: flex;
  margin-top: 0.5vh;
  flex-direction: column;
`;

const StyledToggleArea = styled.div`
  width: 100%;
  height: 5em;
  padding-top: 2.3em;
  border-bottom: 2px solid black;
  position: relative;
`;

const StyledFormArea = styled.div`
  width: 100%;
  padding-top: 2.3em;
  border-bottom: 2px solid black;
  position: relative;
`;

export default LeftPane;
