/**
 * File Description: This component is used to display a single result from the search and enables interaction
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React from 'react';
import styled from 'styled-components';
import { calculateTimeDifference } from '../../../util';
import dummy from '../../../app/assets/dummy.png';
import ButtonComponent from '../../common/Button';
/**
 * 
 * SAMPLE DATA : {
    "arrivalTime": "16:30",
    "date": "2020/11/01",
    "departureTime": "14:00",
    "destination": "Bengaluru (BLR)",
    "flightNo": "AI-111",
    "name": "Air India",
    "origin": "Delhi (DEL)",
    "price": 5217
  }
 */

const FlightResult = ({
  arrivalTime,
  departureTime,
  destination,
  flightNo,
  name,
  origin,
  price,
}) => {
  return (
    <StyledContainer id={`search-results__item ${flightNo}`}>
      <StyledImage src={dummy} alt="Dummy Image" />
      <StyledNameContainer>
        <div id="search-results__flightName" className="title">
          {name}
        </div>
        <div id="search-results__flightNo" className="title">
          {flightNo}
        </div>
      </StyledNameContainer>
      <StyledNameContainer>
        <div id="search-results__departureTime" className="title">
          {departureTime}
        </div>
        <div id="search-results__origin" className="subTitle">
          {origin}
        </div>
      </StyledNameContainer>
      <StyledNameContainer>
        <div id="search-results__arrivalTime" className="title">
          {arrivalTime}
        </div>
        <div id="search-results__destination" className="subTitle">
          {destination}
        </div>
      </StyledNameContainer>
      <StyledNameContainer>
        <div id="search-results__flightDuration" className="title">
          {/*calculateTimeDifference(item.departureTime, item.arrivalTime)*/}
          07h 00m
        </div>
        <div id="search-results__flightType" className="subTitle">
          Non Stop
        </div>
      </StyledNameContainer>
      <StyledPriceContainer>{price}</StyledPriceContainer>
      <div style={{ padding: '1em' }}>
        <ButtonComponent
          variant="danger"
          onClick={() => alert('Booked Successfully!')}
        >
          Book
        </ButtonComponent>
      </div>
    </StyledContainer>
  );
};

const StyledContainer = styled.div`
  display: grid;
  margin-top: 0.5em;
  grid-template-columns: 0.2fr 1fr 1fr 1fr 1fr 1fr 1fr;
  justify-content: center;
  align-items: center;
  border: 1px solid black;
`;

const StyledImage = styled.img`
  width: 75px;
  height: auto;
`;
const StyledNameContainer = styled.div`
  div::first-child {
    background: red;
  }
`;

const StyledPriceContainer = styled.div``;
export default FlightResult;
