/**
 * File Description: This component used to display search results
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React from 'react';
import styled from 'styled-components';
import FlightResult from '../FlightStatus/FlightResult';
import { Accordion, Card, Button } from 'react-bootstrap';

const FlightStatusDisplay = ({ flightsList }) => {
  let { direct, multiple } = flightsList;
  return (
    <StyledContainer id="search-results__container">
      {direct.map((item, index) => (
        <FlightResult key={index} {...item} />
      ))}
      {multiple.map((item, index) => StyledAccordian(item))}
    </StyledContainer>
  );
};

const StyledAccordian = ({ data }) => {
  let { overview } = data;
  //let
  //console.log(data.map((item, index) => console.log('Item', item)));
  return (
    <Accordion>
      <Card>
        <Card.Header>
          <Accordion.Toggle>{/*<FlightResult />*/}</Accordion.Toggle>
        </Card.Header>
        <Accordion.Collapse>
          {/*<Card.Body>
            {data.map((item, index) => (
              <FlightResult key={index} {...item} />
            ))}
            </Card.Body>*/}
        </Accordion.Collapse>
      </Card>
    </Accordion>
  );
  //return null;
};
const StyledContainer = styled.div`
  padding-left: 1em;
  padding-right: 1em;
`;

export default FlightStatusDisplay;
