/**
 * File Description: This component is used to display status of flights
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React, { Fragment, useMemo, useState } from 'react';
import styled from 'styled-components';
import { formatDisplayDate } from '../../util';
const Status = ({
  to = '',
  from = '',
  flightsCount = 0,
  fromDate = '',
  toDate = '',
}) => {
  const [fromDateDisplay, setFromDateDisplay] = useState(fromDate);
  const [toDateDisplay, setToDateDisplay] = useState(toDate);
  useMemo(() => {
    setFromDateDisplay(formatDisplayDate(fromDate));
    //console.log(formatDisplayDate);
  }, [fromDate, toDate]);

  let displayText = from === '' ? '' : to === '' ? '' : `${from} to ${to}`;
  return (
    <StyledStatus id="flight-status___container">
      {displayText !== '' && fromDate !== '' ? (
        <Fragment>
          <div id="flight-status__result">{displayText}</div>
          <StyledStatsDiv id="flight-status__stats">
            <div id="flight-stats__count">{`${flightsCount} flights found`}</div>
            <div id="flight-stats__date">{fromDateDisplay}</div>
          </StyledStatsDiv>
        </Fragment>
      ) : null}
    </StyledStatus>
  );
};

const StyledStatus = styled.div`
  width: 100%;
  height: 5em;
  display: flex;
  justify-items: center;
  align-items: center;
  flex-direction: column;
  border-bottom: 2px solid black;
  position: relative;
  font-size: 1em;
  font-weight: bold;
  color: black;
  padding: 0.2em;
  #flight-status__result {
    width: inherit;
    height: 3em;
    display: flex;
    justify-content: start;
    align-items: center;
  }
`;

const StyledStatsDiv = styled.div`
  display: flex;
  width: 100%;
  position: relative;
  font-size: 1em;
  font-weight: normal;
  color: gray;

  #flight-stats__count {
    margin-right: 1.2em;
  }
`;

export default Status;
