/**
 * File Description: This component is the right pane of the website to display the results of customer
 * Author(s): Sashank Pindiproli
 * Date of Creation: 11/07/2020
 */

import React, { Fragment } from 'react';
import styled from 'styled-components';
import { Card } from 'react-bootstrap';
const RightPane = ({ customerDetails }) => {
  let { name, address, image } = customerDetails;
  if (address.length === 0) address.push('No address found');
  return (
    <Fragment>
      {name !== undefined && image !== undefined ? (
        <StyledContainer id="right-pane__container">
          <StyledCard>
            <Card.Body>
              <Card.Title>{name}</Card.Title>
              <Card.Img src={image}></Card.Img>
              {address.map((item, index) => (
                <Card.Text key={index}>{item}</Card.Text>
              ))}
            </Card.Body>
          </StyledCard>
        </StyledContainer>
      ) : null}
    </Fragment>
  );
};

const StyledContainer = styled.div`
  border: 2px solid black;
  width: calc(59.1%);
  margin-left: 1vh;
  margin-top: 0.5vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledCard = styled(Card)`
  height: 40em;
  width: 40em;
  .card-title {
    font-size: 32px;
    height: 2em;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .card-img {
    width: 25em;
  }
  .card-text {
    height: 3em;
    font-size: 18px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
`;
export default RightPane;
