/**
 * File Description: This component is the left pane of the website to display the results of search
 * Author(s): Sashank Pindiproli
 * Date of Creation: 02/02/2020
 */

import React from 'react';
import styled from 'styled-components';
import ToggleBtnComponent from '../ToggleButton';

const LeftPane = () => {
  const radios = [
    { name: 'One Way', value: '1' },
    { name: 'Return', value: '2' },
  ];
  return (
    <StyledContainer>
      <StyledToggleArea>
        <ToggleBtnComponent radios={radios} defaultVal={radios[0]} />
      </StyledToggleArea>
    </StyledContainer>
  );
};

const StyledContainer = styled.div`
  border: 2px solid black;
  width: calc(33%);
  display: flex;
  margin-top: 0.5vh;
`;

const StyledToggleArea = styled.div`
  background: red;
  width: inherit;
  height: 5em;
  padding-top: 5em;
  justify-content: end;
`;

export default LeftPane;
