/**
 * File Description: This component is used to display a single result from the search and enables interaction
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React from 'react';
import styled from 'styled-components';
import { calculateTimeDifference } from '../../../util';
import dummy from '../../../app/assets/dummy.png';
import ButtonComponent from '../../common/Button';
/**
 * 
 * SAMPLE DATA : {
    "arrivalTime": "16:30",
    "date": "2020/11/01",
    "departureTime": "14:00",
    "destination": "Bengaluru (BLR)",
    "flightNo": "AI-111",
    "name": "Air India",
    "origin": "Delhi (DEL)",
    "price": 5217
  }
 */

const FlightResult = ({
  arrivalTime,
  date,
  departureTime,
  destination,
  flightNo,
  name,
  origin,
  price,
}) => {
  return (
    <StyledContainer id={`search-results__item ${flightNo}`}>
      <StyledImage src={dummy} alt="Dummy Image" />
      <StyledNameContainer>
        <div id="search-results__flightName">{item.name}</div>
        <div id="search-results__flightNo">{item.flightNo}</div>
      </StyledNameContainer>
      <StyledNameContainer>
        <div id="search-results__departureTime">{item.departureTime}</div>
        <div id="search-results__origin">{item.origin}</div>
      </StyledNameContainer>
      <StyledNameContainer>
        <div id="search-results__arrivalTime">{item.arrivalTime}</div>
        <div id="search-results__destination">{item.destination}</div>
      </StyledNameContainer>
      <StyledNameContainer>
        <div id="search-results__flightDuration">
          {/*calculateTimeDifference(item.departureTime, item.arrivalTime)*/}
          07h 00m
        </div>
        <div id="search-results__flightType">Non Stop</div>
      </StyledNameContainer>
      <StyledPriceContainer>{item.price}</StyledPriceContainer>
      <div style={{ padding: '1em' }}>
        <ButtonComponent onClick={() => alert('Booked Successfully!')}>
          Book
        </ButtonComponent>
      </div>
    </StyledContainer>
  );
};

const StyledContainer = styled.div`
  display: grid;
  margin-top: 0.5em;
  grid-template-columns: 0.2fr 1fr 1fr 1fr 1fr 1fr 1fr;
  justify-content: center;
  align-items: center;
  border: 1px solid black;
`;

const StyledImage = styled.img`
  width: 75px;
  height: auto;
`;
const StyledNameContainer = styled.div``;

const StyledPriceContainer = styled.div``;
export default FlightResult;
