      <Accordion key={index}>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button}>Click Me</Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse>
            <Card.Body>Hi</Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    );
  };
  return (
    <StyledContainer id="search-results__container">
      {direct.map((item, index) => (
        <FlightResult key={index} {...item} />
      ))}
      {multiple.map((item, index) => drawAccordion(item, index))}
    </StyledContainer>
  );
};

const StyledContainer = styled.div`
  padding-left: 1em;
  padding-right: 1em;
`;

export default FlightStatusDisplay;
