/**
 * File Description: This component is a loader component to display that the user is still waiting for results
 * Author(s): Sashank Pindiproli
 * Date of Creation: 03/02/2020
 */

import React, { Fragment } from 'react';
import { Button } from 'react-bootstrap';
import styled from 'styled-components';
import { spinner } from './assets/spinner.svg';
const Loader = ({ isWaiting }) => {
  return <Fragment>{isWaiting ? { spinner } : null}</Fragment>;
};

const StyledButton = styled(Button)`
  display: flex;
  color: ${(props) => props.variant};
  width: 100%;
  justify-content: center;
  align-items: center;
`;

export default Loader;
